import {ResultCodeEnum, ResultCodeWithCaptchaEnum, authAPI,securityAPI} from '../components/api/api.ts';
import {stopSubmit} from 'redux-form'

const SET_USER_DATA = 'react/auth/SET_USER_DATA'
const SET_CAPTCHA_URL = 'react/auth/SET_CAPTCHA_URL'

export type initialState = {
    userId:number | null
    email:string | null
    login:string | null
    isAuth:boolean
    captchaUrl:string | null
}
let initialState:initialState =  {
    userId:null,
    email:null,
    login:null,
    isAuth:false,
    captchaUrl:null,
}

export const authReducer = (state:initialState = initialState,action:any):initialState =>{
  switch(action.type) {
    case SET_USER_DATA: 
        return { 
            ...state,
            ...action.data,
        }
    case SET_CAPTCHA_URL: 
    return { 
        ...state,
        ...action.payload
    }
    default: 
        return state;
  }
}
type SetAuthDataAction = {
  userId:number | null
  email:string | null
  login:string | null
  isAuth:boolean
}
type SetAuthUserDataType = {
  type: typeof SET_USER_DATA
  data:SetAuthDataAction
}
export const setAuthUserData = (userId:number | null,email:string | null,login:string | null,isAuth:boolean):SetAuthUserDataType => ({
  type:SET_USER_DATA, data:{userId,email,login,isAuth}
})

type SetCaptchaUrlType = {
  type: typeof SET_CAPTCHA_URL
  payload: {captchaUrl:string}
}
export const setCaptchaUrl= (captchaUrl:string):SetCaptchaUrlType => ({
  type:SET_CAPTCHA_URL, payload:{captchaUrl}
})

type ActionsType = SetCaptchaUrlType | SetAuthUserDataType








export const getData = () => async (dispatch:any) => {
   let response = await authAPI.getData();
    if(response.resultCode === ResultCodeEnum.Success) {
      let {id,login,email} = response.data;
      
      dispatch(setAuthUserData(id, email, login,true));
  }
}
export const login = (email:string,password:string,rememberMe:boolean,captcha:string | any) => async (dispatch:any) => {
  let response = await authAPI.login(email,password,rememberMe,captcha);
  if(response.resultCode === ResultCodeEnum.Success) {
    dispatch(getData())
  } else {
    if (response.resultCode === ResultCodeWithCaptchaEnum.CaptchaIsRequired) {
      dispatch(getCaptchaUrl())
    }
    let message = response.messages.length > 0 ?response.messages[0]:'Some error'
    dispatch(stopSubmit('login',{_error:message}))    
  }
}
export const getCaptchaUrl = () => async (dispatch:any) => {
  let response = await securityAPI.getCaptchaUrl();
  
  const captchaUrl = response.data.url
  dispatch(setCaptchaUrl(captchaUrl))
}
export const loginout = () => async (dispatch:any) => {
    let response = await authAPI.loginout()
    if(response.data.resultCode === 0) {
      dispatch(setAuthUserData(null, null, null,false));
  }
}
export default authReducer;
