import React from 'react'
import s from './formsControls.module.css'
import {Field} from 'redux-form'

export const Textaria = (props) => {
    const {input,meta,...restProps} = props;
    return <FormControl {...props}><textarea {...input} {...restProps}/></FormControl>
}
export const Input = (props) => {
    const {input,meta,...restProps} = props;
    return <FormControl {...props}><input {...input} {...restProps} /></FormControl>
}
const FormControl = ({input,meta:{touched,error},children}) => {
    const hasError = touched && error
    return (
        <div>
            <div className={(hasError?s.error:'')} >{children}</div>
            { hasError && <span className={s.span}>{error}</span>}
        </div>
        
    )
}

export const createField = (placeholder,name,component,validates,props = {},text) => {
    return <div><Field placeholder={placeholder} name={name} component={component} validate={validates} {...props}/>{text}</div>
}
