import React, { useState } from 'react'
import styles from './List.module.css'
import SearchInput from './SearchInput.tsx'
import ListOfTodos from './ListOfTodos.tsx'

type Todo = {
    id:number
    title:string
    data:string
    completed:boolean
}
const List = () => {
    const [todoTitle,setTodoTitle] = useState('')
    const [todos,setTodos] = useState([
        {id:1,title:'First todo',data:'5',completed:false},
        {id:2,title:'Second todo',data:'5',completed:false}
    ] as Array<Todo>)
    const addTodo = (event) => {
        if(event.key === 'Enter') {
            const options = {
                weekday: "long",
                year: "numeric",
                month: "long",
                day: "numeric",
              };
            setTodos([
                ...todos,
                {
                    id:todos[todos.length - 1].id + 1,
                    title:todoTitle,
                    data:new Date().toLocaleDateString("en-KZ",options),
                    completed:false
                }
            ])
        }
    }
    return(
        <div className={styles.list}>
            <div className={styles.form}>
                <input type="text" 
                    placeholder="Create new part of your list" 
                    value={todoTitle} 
                    onChange={(event) => setTodoTitle(event.target.value)} 
                    className={styles.searchInput+" "+styles.inputs}
                    onKeyDown={event => addTodo(event)}
                    />
                <input 
                    type="submit" 
                    className={styles.submit+" "+styles.inputs} 
                    value="ADD" 
                    onClick={event => addTodo(event)}
                    />
            </div>
            <ListOfTodos todos={todos}/>
        </div>
    )
}

export default List
