import so from './MyPosts.module.css';
import Post from './Post/Post';
import React from 'react';
import {reduxForm,Field} from 'redux-form'
import {maxLengthCreator, requered} from './../../../utials/validators/validators'
import {Textaria} from './../../common/formsControls/formsControsl'

const maxLength10 = maxLengthCreator(10);
const MyPostsForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <Field component={Textaria} name='post' placeholder='Пост' value={props.newPostText} validate={[requered,maxLength10]} />
            <br />
            <button>Add post</button>
        </form>
    )
}

const MyPostsReduxForm = reduxForm({
    form:'ProfileAddPostForm'
})(MyPostsForm)

window.props = []
class MyPosts extends React.Component {
componentDidMount() {setTimeout(() => {
    this.setState({a:12})
}, 3000);}

    shouldComponentUpdate(nextProps,nextState) {
        return nextProps !== this.props || nextState !== this.state
    }

    render () {
        console.log('RENDER')
        window.props.push(this.props)
        console.log(this.props)
        let postsElements = this.props.posts.map( post => <Post message={post.message} key={post.id} likecount={post.likesCount}/>)
        const onSubmit = (formData) => {
            this.props.addPost(formData.post)
        }   
        let newPostElement = React.createRef();
    
        let onAddPosts = () => { 
            this.props.addPost()
        }
        
        let onPostChange = () => {
            let text = newPostElement.current.value;
            this.props.updateNewPostText(text);
        }
        return (
            <div className={so.postsBlock}>
                <div>
                    My posts
                </div>
                    <MyPostsReduxForm onSubmit={onSubmit}/>
                <div className={so.posts}>
                    {postsElements}
                </div>
            </div>
        )
    }
}
export default MyPosts
