import React from 'react';
import { NavLink } from 'react-router-dom';
import s from './Users.module.css';
import axios from 'axios'

let Users = (props) => {

    
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for(let cnt = 1; cnt <= pagesCount; cnt++){
        pages.push(cnt);
        
    }
        return (
            <section>
                <h2>{props.h}</h2>
                <ol className={s.ol}>
                    { pages.map (p => {
                        return <li className = {props.currentPage === p && s.selectedPage} onClick={(e) => {props.onPageChanged(p)}}>{p}</li>
                    })}
                </ol>
                <ul>
                    {
                    props.users.map( u => <li className={s.li} key={u.id}>
                        <span>
                            <NavLink  to={'/profile/'+u.id} >
                                <img className={s.img} src={u.photos.small != null?u.photos.small:'/userNull.jpg'} alt="ava"/>
                            </NavLink>
                            {u.followed?
                            <button disabled={props.folowingInProgress} onClick={() => {
                                props.toggleIsFollowing(true)
                                axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{withCredentials:true,headers:{'API-KEY':'cafd4f76-51d0-40a7-93fa-907a2586d1e6'}}).then(response => {
                                    if(response.data.resultCode === 0) {
                                     props.unfollow(u.id)
                                    }
                                    props.folowingInProgress(false)
                                  });

                            }}> Unfollow</button>:
                            <button disabled={props.folowingInProgress} onClick={() => {
                                props.folowingInProgress(true)
                                axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{},{withCredentials:true,headers:{'API-KEY':'cafd4f76-51d0-40a7-93fa-907a2586d1e6'}}).then(response => {
                                  if(response.data.resultCode === 0) {
                                    props.follow(u.id)
                                  }
                                  props.folowingInProgress(false)
                                });
                            }}>Follow</button>}     
                        </span>
                        <span>
                            <b>{u.name}</b>
                            <p>{u.status}</p>
                            <span>{"u.location.city"}</span>
                            <span>{"u.location.country"}</span>
                        </span>
                    </li>)
                    }
                </ul>
            </section>
        )
    }

export default Users;
