import React  from 'react'
import {useDispatch,useSelector} from 'react-redux'
import styles from '../FormCreateTable.module.css'
import { selectDays, selectItems } from '../../../../redux/createTable-selectors.ts'
import {useNavigate} from 'react-router-dom'
import { actions } from '../../../../redux/tables-reducer'
import {v1} from 'uuid'

const Data = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const items = useSelector(selectItems)
    const days = useSelector(selectDays)

    const removeItem = (value:string) => {
        dispatch(actions.deleteItem(value))
    }
    const createTable = () => {
        if(parseInt(days) < 0 || parseInt(days) > 30 )  return
        if(items.length < 1)  return
        dispatch(actions.setNewTable({
            id:v1(),
            tableData:{

        }}))
        navigate('/tables')
    }
    return (
        <>
            <section className={styles.section}>
                <div className={styles.items}>
                    <b className={styles.b}>Your`s types:</b>
                    {items && <ol>
                        {items.map(item => <li className={styles.li} key={item} onClick={(e) => removeItem(e.currentTarget.innerHTML)}>{item}</li>)}
                    </ol>}
                </div>
                <div className={styles.days}>
                    <b className={styles.b}>Days: {days}</b>
                </div>
            </section>
            <input type="submit" value="Create" className={styles.inputs + ' ' + styles.submit} onClick={ () => createTable()}/>
        </>
    )
}

export default Data
