import React from 'react';
import s from './Dialogs.module.css';
import Message from './Message/Message';
import DialogItem from './DialogItem/DialogItem';
import {reduxForm,Field} from 'redux-form'
import {Textaria} from './../common/formsControls/formsControsl'
import {maxLengthCreator, requered} from './../../utials/validators/validators'

const maxLength20 = maxLengthCreator(20);
const Dialogs = (props) =>{

    let state = props.dialogsPage;

    let dialogsElements = state.dialogs
        .map( dialog => <DialogItem name={dialog.name} key={dialog.id} id={dialog.id} />);
    
    let messagesElements = state.messages
        .map( message => <Message message={message.message} key={message.id}/>);  
        


        
    let onSubmit = (formData) => {
        props.addMessage(formData.message);
    }
        return (
        <section>
            <h2>{props.h}</h2>
            <div className={s.parent}>
                    <ul  className={s.dialogs}>
                        {dialogsElements}
                    </ul>
                    <ul className={s.messages}>
                        {messagesElements}
                        <MessageReduxForm onSubmit={onSubmit}/>
                    </ul>

            </div>

        </section>
    )
}
let dialogForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <Field name='message' component={Textaria} placeholder={'Enter your message'} validate={[requered,maxLength20]}/>
        <br />
        <button>Send</button>
        </form>
    )
}
let MessageReduxForm = reduxForm({
    form:'DialogAddMessageForm'
})(dialogForm)

export default Dialogs
