import React ,{useEffect,useState}from 'react'
import { Avatar, Button } from 'antd';




export type ChatMessageType =   {
    message:string
    photo:string
    userId:number
    userName:string
  }
const ChatPage:React.FC = () => {

    return (
        <Chat />
    )
}
const Chat:React.FC = () => {
    const [] = useState<WebSocket | null>()
    let ws:WebSocket;
    useEffect(() => {
        function createChannel() {
        ws = new WebSocket('wss://social-network.samuraijs.com/handlers/ChatHandler.ashx') 
        }
        createChannel();
        ws.addEventListener('close',() => {
            console.log('close')
        })
    },[])
    return ( 
    <div>
        <Messages ws={ws}/>
        <AddMessageForm />
     </div>
    )
}
const Messages:React.FC<{ws:WebSocket}> = ({ws}) => {

    const [messages,setMessages] = useState<Array<ChatMessageType>>([])    
    useEffect(() => {
        ws.addEventListener('message',(e:MessageEvent) => {
            setMessages((prevMessages) => [...prevMessages,  ...JSON.parse(e.data)])
        })
    },[])

    return ( 
    <div style={{height:'400px',overflowY:'auto'}}>
        {messages.map((m,index) => <Message key={index} message={m}/>)}
     </div>
    )
}
const Message:React.FC<{message:ChatMessageType}> = ({message}) => {
    return (<div>
        {message.photo ? <img src={message.photo} alt="ava" style={{width:'45px',marginRight:'10px'}}/> : <Avatar>U</Avatar>}<b>{message.userName}</b>
        <br />
        {message.message}
        <hr />
    </div>)
}
const AddMessageForm:React.FC<{ws:WebSocket}> = ({ws}) => {
    const [message,setMessage] = useState('')
    const [readyStatus,setReadyStatus] = useState<'pending' | 'ready'>('pending')
    useEffect(() => {
        ws.addEventListener('open',() => {
            setReadyStatus('ready')
        })
    },[])
    const sendMessage = () => {
        if(!message) return
        ws.send(message)
        setMessage('')
    }
    return ( 
    <div>
        <div><textarea onChange={(e) => setMessage(e.currentTarget.value)} value={message}></textarea></div>
        <Button onClick={sendMessage} disabled={readyStatus !== 'ready'}>Send</Button>
     </div>
    )
}

export default ChatPage
