import { Todos } from "../api/todos/todo-api.ts"
import { Todo } from "../components/planPage/types/types.ts"
import { InferActionsType } from "./redux-store.ts"
import { ThunkType } from "./tables-reducer.ts"

type ActionsType = InferActionsType<typeof actions>
type InitialStateType = typeof initialState

const SET_NEW_TODO = 'SET_NEW_PURPOSE'
const DELETE_TODO = 'DELETE_PURPOSE'
const SET_STATUS_TODO = 'SET_STATUS_TODO'
const SET_FOLOWWING_PROGRESS = 'SET_FOLOWWING_PROGRESS'

const initialState = {
    todos:[] as Array<Todo>,
    progress:false
}

const planReducer = (state:InitialStateType = initialState,action:ActionsType):InitialStateType => {
    switch(action.type) {
        case SET_NEW_TODO : {
            return { ...state,
                todos:[action.todo,...state.todos]
            }
        }
        case DELETE_TODO : {
            return {
                ...state,
                todos:state.todos.filter((item) => item._id != action.todoId)
            }
        }
        case SET_STATUS_TODO : {
            return {
                ...state,
                todos:state.todos.filter((todo) => {
                    if(todo._id === action.todoId) {
                        todo.completed = !todo.completed
                    }
                    return todo
                })
            }
        }
        case SET_FOLOWWING_PROGRESS : {
            return {
                ...state,
                progress:action.progress
            }
        }
        default: {
            return state;
        }
    }
}

export const actions = {
    setNewTodo:(todo:Todo) => ({type:SET_NEW_TODO,todo:todo}),
    deleteTodo:(todoId:number) => ({type:DELETE_TODO,todoId:todoId}),
    setStatus:(todoId:number) => ({type:SET_STATUS_TODO,todoId:todoId}),
    followingProgress:(progress:boolean) => ({type:SET_FOLOWWING_PROGRESS,progress:progress}),
}

export const getTodos = ():ThunkType  => {
    return async (dispatch) => {
        dispatch(actions.followingProgress(false))
        let response = await Todos.getTodos()
        if(response.data.status === 1) {
            response.data.data.map((todo:Todo) => {
                dispatch(actions.setNewTodo(todo))
            }) 
        }
        dispatch(actions.followingProgress(true))
    }
}
export const setTodo = (todo:Todo):ThunkType => {
    return async (dispatch) => {
        const response = await Todos.setTodo(todo)
        if(response.data.status === 1) {
            dispatch(actions.setNewTodo(response.data.data))
        }
    }
}
export default planReducer
