import {instanse,ResponseType, ResultCodeEnum, ResultCodeWithCaptchaEnum} from './api.ts'

type MeResponseType = {
    id:number
    email:string
    login:string
}
type LoginResponseType = {
    userId:number
    email:string
    login:string
}

export const authAPI = {
    getData() {
        return instanse.get<ResponseType<MeResponseType>>(`auth/me`).then(response => response.data);
    },
    login(email:string,password:string,rememberMe:boolean = false,captcha: null | string = null) {
        return instanse.post<ResponseType<LoginResponseType,ResultCodeEnum | ResultCodeWithCaptchaEnum >>(`auth/login`,{email,password,rememberMe,captcha}).then(res => res.data);
    },
    loginout() {
        return instanse.delete(`auth/login`);
    },
}
