import React, { useContext, useState,useEffect } from 'react'
import styles from './List.module.css'
import { Todo } from './List'
import { Context } from '../context.ts'

const TodoItem = ({todo}) => {
    const [checked,setChecked] = useState(todo.completed)
    //@ts-ignore
    const {removeTodo,toggleTodo} = useContext(Context)
    const cls = ['title']
    if(todo.completed) {
        cls.push('completed')
    }
    return (
        <div className={styles.todo}>
            <div className={styles.checkbox}>
                <input type="checkbox" checked={checked} onChange={() => toggleTodo(todo.id)}/>
            </div>
            <div>
                <b className={cls.join(' ')}>Your todo: {todo.title}</b>
                <p className={styles.data}>Date: {todo.data}</p>
                <button className={styles.button} onClick={() => removeTodo(todo.id)}>Ã—</button>
            </div>
        </div>
    )
}


export default TodoItem
