import React from 'react';
import {follow,unfollow,setCurrentPage,toggleFollowingProgress,getUsers} from './../../redux/users-reducer'
import {connect}  from 'react-redux'
import Users from './Users.jsx';
import Preloader from './../common/preloader/Preloader';
import {compose} from 'redux'
import {getUsersSuperSelector,getCurrentPage,getFolowingInProgress,getIsFetching,getTotalUsersCount,getPageSize} from './../../redux/users-selectors'

class UsersContainer extends React.Component {
    componentDidMount() {
        let {currentPage,pageSize} = this.props
        this.props.getUsers(currentPage,pageSize);
/*
        this.props.toggleIsFetching(true)
        userAPI.getUsers(this.props.currentPage,this.props.pageSize).then(data => {
            this.props.toggleIsFetching(false)
            this.props.setUsers(data.items);
            this.props.setTotalUsersCount(data.totalCount);
        });
*/
    }
    onPageChanged = (pageNumber) => {
        let {pageSize} = this.props
        this.props.getUsers(pageNumber,pageSize);
/*
        this.props.toggleIsFetching(true)
        this.props.setCurrentPage(pageNumber);
        userAPI.getUsers(pageNumber,this.props.pageSize).then(data => {
            this.props.toggleIsFetching(false)
            this.props.setUsers(data.items);
        });
*/
    }
    render() {

        return <>
        //{this.props.isFetching?<Preloader/>:null}
            <Users 
                totalUsersCount={this.props.totalUsersCount}
                pageSize={this.props.pageSize}
                currentPage={this.props.currentPage}
                onPageChanged={this.onPageChanged}
                users={this.props.users}
                follow={this.props.follow}
                unfollow={this.props.unfollow}
                h={this.props.h}
                folowingInProgress={this.props.folowingInProgress}
            />
        </>
    }
}
/*
let mapStateToProps = (state) => {
    return {
        users : state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage:state.usersPage.currentPage,
        isFetching:state.usersPage.isFetching,
        folowingInProgress:state.usersPage.folowingInProgress
    }
}
*/
let mapStateToProps = (state) => {
    return {
        users : getUsersSuperSelector(state),
        pageSize: getPageSize(state),
        totalUsersCount: getTotalUsersCount(state),
        currentPage: getCurrentPage(state),
        isFetching: getIsFetching(state),
        folowingInProgress: getFolowingInProgress(state)
    }
}


export default compose(
    connect(mapStateToProps,{follow,unfollow,setCurrentPage,toggleFollowingProgress,getUsers:getUsers}),

)(UsersContainer)
