import React, { useEffect ,}  from 'react'
import styles from './Table.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { Navigate, useParams } from 'react-router-dom'
import { selectTables } from '../../../../redux/tables-selectors'
import { actions, tableFullType } from '../../../../redux/tables-reducer'
import {v1} from 'uuid'

const Table = () => {
    const dispatch = useDispatch()
    const {tableId} = useParams()
    const tables = useSelector(selectTables)
    const tableData:tableFullType | undefined = tables.find(table => table._id === tableId)
    console.log(tableData)
    if(tableData === undefined) {
        window.location.href = "/tables"
        return 
    }
    if(tableData.tableData.days[0] !== 'ㅤ') tableData.tableData.days.unshift('ㅤ')
    const changeValue = (row:number,column:number,newValue:string) => {
        let updatedData = tableData.tableData.data.map((rowData, index) => {
            if (index === row) {
                let updatedStatus = [...rowData.status]; 
                if (newValue === "-") {
                    updatedStatus[column] = "+";
                } else if (newValue === "+") {
                    updatedStatus[column] = "-";
                }
                return {
                    ...rowData,
                    status: updatedStatus,
                };
            }
            return rowData;
        });
    
        const updatedTableData = {
            ...tableData.tableData,
            data: updatedData,
        };
    
        const updatedTable = {
            ...tableData,
            tableData: updatedTableData,
        };
        dispatch(actions.updateTable(updatedTable));
    }
    return (
        <>
        {!tableData ? <Navigate to="/tables" /> :
        <div className={styles.container}>
            <h2>{tableData.title}</h2>const deleteTable = (id:string) => {
        dispatch(deleteTable(id))
    }
            <table className={styles.table}>
                <thead>
                    <tr>
                        {tableData.tableData.days.map(day => <th key={day} className={styles.th}>{day}</th>)}
                    </tr>
                </thead>
                <tbody>
                    {tableData.tableData.data.map((rowData,indexRow) => {
                        return (
                            <tr key={v1()}> 
                                <td className={styles.td}>{rowData.name}</td>
                                {rowData.status.map((value,indexValue) => {
                                    return <td key={v1()} className={styles.td} onClick={(event) => changeValue(indexRow,indexValue,event.currentTarget.innerHTML)}>{value}</td>
                                })}
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        </div>}
        </>
        
    )
}

export default Table
