import React ,{useEffect,useState}from 'react'
import { Avatar, Button } from 'antd';
import { useDispatch } from 'react-redux';
import { startMessagesListening } from '../../redux/chat-reducer';

export type ChatMessageType =   {
    message:string
    photo:string
    userId:number
    userName:string
  }
const ChatPage:React.FC = () => {
    return (
        <Chat />
    )
}
const Chat:React.FC = () => {
    let dispatch = useDispatch()
    useEffect(() => {
        dispatch(startMessagesListening())
    },[])
    d
    return (    
    <div> 
        <Messages ws={ws} />
        <AddMessageForm ws={ws} />
     </div>
    )
}
const Messages:React.FC<{ws:WebSocket | null}> = ({ws}) => {
    const [messages,setMessages] = useState<Array<ChatMessageType>>([])  
    useEffect(() => {
        let messageHandler = (e:MessageEvent) => {
            let newMessages = JSON.parse(e.data)
            setMessages((prevMessages) => [...prevMessages,  ...newMessages])
        }
        ws?.addEventListener('message',messageHandler)
        return () => {
            ws?.removeEventListener('message',messageHandler) 
         }
    },[ws])

    return ( 
    <div style={{height:'400px',overflowY:'auto'}}>
        {messages.map((m,index) => <Message key={index} message={m}/>)}
     </div>
    )
}
const Message:React.FC<{message:ChatMessageType}> = ({message}) => {
    return (<div>
        <img src={message.photo} alt="ava" style={{width:'45px',marginRight:'10px'}}/><b>{message.userName}</b>
        <br />
        {message.message}
        <hr />
    </div>)
}
const AddMessageForm:React.FC<{ws:WebSocket | null}> = ({ws}) => {
    const [message,setMessage] = useState('')
    const [readyStatus,setReadyStatus] = useState<'pending' | 'ready'>('pending')
    useEffect(() => {
        let openHabler = () => {
            setReadyStatus('ready')
        }
        ws?.addEventListener('open',openHabler)
        return () => {
           ws?.removeEventListener('open',openHabler) 
        }
    },[ws])
    const sendMessage = () => {
        if(!message) return
        ws?.send(message)
        setMessage('')
    }
    return ( 
    <div>
        <div><textarea onChange={(e) => setMessage(e.currentTarget.value)} value={message}></textarea></div>
        <Button onClick={sendMessage} disabled={readyStatus !== 'ready'}>Send</Button>
     </div>
    )
}

export default ChatPage
