    return (
        <form onSubmit={props.handleSubmit}>
            <Field component={'input'} placeholder='Пост' value={props.newPostText} />
            <button>Add post</button>
        </form>
    )
}

const MyPostsReduxForm = reduxForm({
    form:'post'
})(MyPostsForm)
