import { Dispatch } from 'redux';
import {userAPI} from '../components/api/api';
import {updateObjectInArray} from '../utials/object-helpers'
import {UserType} from './../types/types'
import { AppStateType } from './redux-store';
import { ThunkAction } from 'redux-thunk';

const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET_USERS'
const SET_CURRENT_PAGE = ' SET_CURRENT_PAGE'
const SET_TOTAL_USERS_COUNT = 'SET_TOTAL_USERS_COUNT'
const TOGGLE_IS_FETCHING = 'TOGGLE_IS_FETCHING'
const TOGGLE_IS_FOLLOWING_PROGRESS = 'TOGGLE_IS_FOLLOWING_PROGRESS'

let initialState = {
  users:[] as Array<UserType>,
  pageSize: 5,
  totalUsersCount: 0,
  currentPage:1,
  isFetching:true,
  folowingInProgress:[] as Array<number> //array of users id
}
type initialStateType = typeof initialState

const usersReducer = (state:initialStateType = initialState,action:ActionsType):initialStateType =>{
    switch(action.type){
      
        case FOLLOW: {
          return { 
            ...state, 
            users: updateObjectInArray(state.users,action.userId,'id',{followed:true})
          }
        }
        case UNFOLLOW: {
          return { 
            ...state, 
            users: updateObjectInArray(state.users,action.userId,'id',{followed:false})
          }
        }
        case SET_USERS : {
          return {
            ...state, users:action.users 
          }
        }
        case SET_CURRENT_PAGE : {
          return {
            ...state, currentPage:action.currentPage 
          }
        }
        case SET_TOTAL_USERS_COUNT : {
          return {
            ...state, totalUsersCount:action.totalUsersCount
          }
        }
        case TOGGLE_IS_FETCHING : {
          return {
            ...state, isFetching:action.isFetching
          }
        }
        case TOGGLE_IS_FOLLOWING_PROGRESS : {
          return {
            ...state, folowingInProgress:action.isFetching
            ?[...state.folowingInProgress ,action.userId]
            :[state.folowingInProgress.filter(id => id !== action.userId)]
          }
        }
        default: {
            return state;
        }
  }
}

type ActionsType = acceptFollowType | acceptUnfollowType |
 setUsersACType | setCurrentPageType | setTotalUsersCountType |
 toggleIsFetchingType | toggleFollowingProgressType



type acceptFollowType = {
  type:typeof FOLLOW
  userId:number
}
export const acceptFollow = (userId:number):acceptFollowType => ({type:FOLLOW , userId})
type acceptUnfollowType = {
  type:typeof UNFOLLOW
  userId:number
}
export const acceptUnfollow = (userId:number):acceptUnfollowType => ({type:UNFOLLOW,userId})
type setUsersACType = {
  type:typeof SET_USERS
  users:Array<UserType>
}
export const setUsersAC = (users:Array<UserType>):setUsersACType => ({type:SET_USERS,users})
type setCurrentPageType = {
  type:typeof SET_CURRENT_PAGE
  currentPage:number
}
export const setCurrentPage = (currentPage:number):setCurrentPageType => ({type:SET_CURRENT_PAGE, currentPage})
type setTotalUsersCountType = {
  type:typeof SET_TOTAL_USERS_COUNT
  totalUsersCount:number
}
export const setTotalUsersCount = (totalUsersCount:number):setTotalUsersCountType => ({type:SET_TOTAL_USERS_COUNT, totalUsersCount})
type toggleIsFetchingType = {
  type:typeof TOGGLE_IS_FETCHING
  isFetching:boolean
}
export const toggleIsFetching = (isFetching:boolean):toggleIsFetchingType => ({type:TOGGLE_IS_FETCHING, isFetching})
type toggleFollowingProgressType = {
  type:typeof TOGGLE_IS_FOLLOWING_PROGRESS
  isFetching:boolean
  userId:number
}
export const toggleFollowingProgress = (isFetching:boolean,userId:number):toggleFollowingProgressType => ({type:TOGGLE_IS_FOLLOWING_PROGRESS, isFetching,userId})






type DispatchType = Dispatch<ActionsType>
type AppState = () => AppStateType

export const getUsers = (currentPage:number,pageSize:number):ThunkAction<Promise<void>,AppState,unkno> => { 
  return async (dispatch,getState) => {
    dispatch(toggleIsFetching(true))
    dispatch(setCurrentPage(currentPage))
    let data = await userAPI.getUsers(currentPage,pageSize)
        dispatch(toggleIsFetching(false))
        dispatch(setUsersAC(data.items));
        dispatch(setTotalUsersCount(data.totalCount));
  }
}

const followUnfollowFlow = async (dispatch:any,userId:number,apiMethod:any,AC:any) => {
  dispatch(toggleFollowingProgress(true,userId));
  let response = await apiMethod(userId)
    if(response.data.resultCode === 0) {
      dispatch(AC(userId));
    }
  dispatch(toggleFollowingProgress(false,userId));
}

export const follow = (userId:number) => { 
  return async (dispatch:any) =>  {
    followUnfollowFlow(dispatch,userId,userAPI.follow.bind(userAPI),acceptFollow);
  }
}
export const unfollow = (userId:number) => { 
  return async (dispatch:any) =>  {
    followUnfollowFlow(dispatch,userId,userAPI.unfollow.bind(userAPI),acceptUnfollow);
  }
}
export default usersReducer;
