import React, { useState }  from 'react'
import {useDispatch,useSelector} from 'react-redux'
import styles from './FormCreateTable.module.css'
import { actions } from '../../../redux/createTable-reducer.ts'
import { selectDays, selectItems } from '../../../redux/createTable-selectors.ts'
import ItemsInput from './InputsForm/ItemsInput.tsx'
import DaysInput from './InputsForm/DaysInput.tsx'
import Data from './FinishData/Data'

const FormCreateTable = () => {
    const dispatch = useDispatch()
    const items = useSelector(selectItems)
    const days = useSelector(selectDays)
    const removeItem = (value:string) => {
        dispatch(actions.deleteItem(value))
    }
    return (
        <div className={styles.formCreateTable}>
            <form className={styles.form}>
                <ItemsInput />
                <DaysInput />
                <input type="submit" value="Create" className={styles.inputs + ' ' + styles.submit}/>
            </form>
            <h2 className={styles.h2}>Finish data</h2>
            <Data />
        </div>
    )
}

export default FormCreateTable


