import React from 'react';
import s from './Paginator.module.css';
import {useState} from 'react'

let Paginator = (props,portionSize = 3) => {
    let pagesCount = Math.ceil(props.totalItemsCount / props.pageSize);
    let pages = [];
    for(let cnt = 1; cnt <= pagesCount; cnt++){
        pages.push(cnt);
    }

    let portionCount = Math.ceil(pagesCount / portionSize);
    let [portionNumber,setPortionNumber] = useState(1)
    let leftPortionPageNumber = (props.currentPage - 1) * props.pageSize + 1
    let rightPortionPageNumber = props.currentPage * props.pageSize
    console.log(leftPortionPageNumber,rightPortionPageNumber)


    return (
    <>
    {portionNumber > 1 && <button></button>}
    <ol className={s.ol}>
        { pages.map (p => {
            return <li className = {props.currentPage === p && s.selectedPage} onClick={(e) => {props.onPageChanged(p)}}>{p}</li>
        })}
    </ol>
    
    </>
    )
}

export default Paginator;
