import React ,{useEffect,useRef,useState}from 'react'
import { Avatar, Button } from 'antd';
import { useDispatch ,useSelector} from 'react-redux';
import { startMessagesListening ,sendMessageThunk,stopMessagesListening} from '../../redux/chat-reducer.ts';
import { AppStateType } from '../../redux/redux-store';

export type ChatMessageType =   {
    message:string
    photo:string
    userId:number
    userName:string
  }
const ChatPage:React.FC = () => {
    return (
        <Chat />
    )
}
const Chat:React.FC = () => {
    let dispatch = useDispatch()
    let status = useSelector((state:AppStateType)=> state.chat.status)
    
    useEffect(() => {
        dispatch(startMessagesListening())
        return () => {
            dispatch(stopMessagesListening())
        }
    },[])
    return (    
    <div> 
        {status === 'error' && <div>Error</div>}
            <Messages/>
            <AddMessageForm />
     </div>
    )
}
const Messages:React.FC = () => {
    const messages = useSelector((state:AppStateType) => state.chat.messages)
    const messagesRef = useRef<HTMLDivElement>(null)
    const autoScrollIsActive = useState()
        
    useEffect(() => {
        messagesRef.current?.scrollIntoView({behavior:'smooth'})
    },[messages])


    return ( 
    <div style={{height:'400px',overflowY:'auto'}}>
        {messages.map((m,index) => <Message key={index} message={m}/>)}
        <div ref={messagesRef}></div>
     </div>
    )
}
const Message:React.FC<{message:ChatMessageType}> = ({message}) => {
    return (<div>
        <img src={message.photo} alt="ava" style={{width:'45px',marginRight:'10px'}}/><b>{message.userName}</b>
        <br />
        {message.message}
        <hr />
    </div>)
}
const AddMessageForm:React.FC = () => {
    const [message,setMessage] = useState('')
    const status = useSelector((state:AppStateType) => state.chat.status)
    console.log(status)
    const dispatch = useDispatch()
    
    const sendMessage = () => {
        if(!message) return
        dispatch(sendMessageThunk(message))
        setMessage('')
    }
    return ( 
    <div>
        <div><textarea onChange={(e) => setMessage(e.currentTarget.value)} value={message}></textarea></div>
        <Button onClick={sendMessage} disabled={status != 'ready'}>Send</Button>
     </div>
    )
}

export default ChatPage
