import { Todo } from "../components/planPage/types/types"
import { InferActionsType } from "./redux-store"

type ActionsType = InferActionsType<typeof actions>
type InitialStateType = typeof initialState

const SET_NEW_TODO = 'SET_NEW_PURPOSE'
const DELETE_TODO = 'DELETE_PURPOSE'
const SET_STATUS_TODO = 'SET_STATUS_TODO'

const initialState = {
    todos:[] as Array<Todo>
}

const planReducer = (state:InitialStateType = initialState,action:ActionsType):InitialStateType => {
    switch(action.type) {
        case SET_NEW_TODO : {
            return { ...state,
                todos:[action.todo,...state.todos]
            }
        }
        case DELETE_TODO : {
            return {
                ...state,
                todos:state.todos.filter((item) => item.id != action.todoId)
            }
        }
        case SET_STATUS_TODO : {
            return {
                ...state,
                todos:state.todos.filter((todo) => {
                    if(todo.id === action.todoId) {
                        todo.completed = !todo.completed
                    }
                    return todo
                })
            }
        }
        default: {
            return state;
        }
    }
}

export const actions = {
    setNewTodo:(todo:Todo) => ({type:SET_NEW_TODO,todo}),
    deleteTodo:(todoId:number) => ({type:DELETE_TODO,todoId}),
    setStatus:(todoId:number) => ({type:SET_STATUS_TODO,todoId}),
}


export const getTodos = () => (dispatch) => {
    
}
export default planReducer
