import './App.css';
import React from 'react'
import HeaderContainer from './components/header/HeaderContainer.tsx';
import Navbar from './components/navbar/Navbar.tsx';
import UsersPage from './components/users/UsersContainer.tsx';
import Login from './components/login/Login.tsx';
import {BrowserRouter,Route, Routes,Navigate} from 'react-router-dom';
import {connect} from 'react-redux'
import {initializeApp} from './redux/app-reducer.ts'
import {compose} from 'redux'
import Preloader from './components/common/preloader/Preloader.tsx'
import store, { AppStateType } from './redux/redux-store.ts'
import {Provider} from 'react-redux';
import {Suspense} from 'react'
import 'antd/dist/reset.css';
import { LaptopOutlined, NotificationOutlined, UserOutlined } from '@ant-design/icons';
import type { MenuProps } from 'antd';
import { Breadcrumb, Layout, Menu, theme } from 'antd';

const { Header, Content, Footer, Sider } = Layout;
const DialogsContainer = React.lazy(() => import('./components/dialogs/DialogsContainer.tsx'))
const ProfileContainer = React.lazy(() => import('./components/profile/ProfileContainer.tsx'))
const items1: MenuProps['items'] = ['1', '2', '3'].map((key) => ({
  key,
  label: `nav ${key}`,
}));

const items2: MenuProps['items'] = [UserOutlined, LaptopOutlined, NotificationOutlined].map(
  (icon, index) => {
    const key = String(index + 1);

    return {
      key: `sub${key}`,
      icon: React.createElement(icon),
      label: `subnav ${key}`,

      children: new Array(4).fill(null).map((_, j) => {
        const subKey = index * 4 + j + 1;
        return {
          key: subKey,
          label: `option${subKey}`,
        };
      }),
    };
  },
);
const {
  token: { colorBgContainer },
} = theme.useToken();

type DispatchPropsType = {
  initializeApp:() => void
}
type MapPropsType = ReturnType<typeof mapStateToProps>

class App extends React.Component<DispatchPropsType & MapPropsType> {
  catchAllUnhandleErrors = (e: PromiseRejectionEvent) => {
    alert('Problem')
  }
  componentDidMount() {
    this.props.initializeApp();
    window.addEventListener("unhandledrejection",this.catchAllUnhandleErrors)
  }
  componentWillUnmount() {
    window.removeEventListener("unhandledrejection",this.catchAllUnhandleErrors)
  }
  render() {
    if(!this.props.initialized) {
      return <Preloader />
    }
    return (
    <Layout>
      <Header className="header">
        <div className="logo" />
        <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['2']} items={items1} />
      </Header>
      <Content style={{ padding: '0 50px' }}>
        <Breadcrumb style={{ margin: '16px 0' }}>
          <Breadcrumb.Item>Home</Breadcrumb.Item>
          <Breadcrumb.Item>List</Breadcrumb.Item>
          <Breadcrumb.Item>App</Breadcrumb.Item>
        </Breadcrumb>
        <Layout style={{ padding: '24px 0', background: colorBgContainer }}>
          <Sider style={{ background: colorBgContainer }} width={200}>
            <Menu
              mode="inline"
              defaultSelectedKeys={['1']}
              defaultOpenKeys={['sub1']}
              style={{ height: '100%' }}
              items={items2}
            />
          </Sider>
          <Content style={{ padding: '0 24px', minHeight: 280 }}>


          </Content>
        </Layout>
      </Content>
      <Footer style={{ textAlign: 'center' }}>Ant Design Â©2023 Created by Ant UED</Footer>
    </Layout>
      /*<div >
        <header>
          <HeaderContainer />
        </header>
        <main>
          <Navbar /*sidebar={props.state.sidebarPage.sidebar}/>
          <Suspense fallback={<div><Preloader /></div>}>
          <Routes>
                <Route path='/' element={<Navigate to="/profile" />}/>
                <Route path='/profile/:userId?' element={<ProfileContainer h='Profile'/>}/>
                <Route path='/dialogs/*' element={<DialogsContainer h='Dialogs'/>}/>
                <Route path='/users' element={<UsersPage h='Users'/>}/>
                <Route path='/login' element={<Login h='Login'/>}/>
                <Route path='*' element={<div>404 NOT FOUND<Button type={"primary"}>OK</Button></div>}/>

          </Routes>
          </Suspense>
        </main>
      </div>*/
    );
}
}

const mapStateToProps = (state:AppStateType) => ({
  initialized:state.app.initialized
})
let AppContainer = compose<React.ComponentType>(
  connect(mapStateToProps,{initializeApp}),
)(App);

let AppMain:React.FC = () => { 
  return <BrowserRouter>
  <Provider store={store}>
    <AppContainer />
  </Provider>
</BrowserRouter>
}

export default AppMain
