import React from 'react';
import { NavLink } from 'react-router-dom';
import s from './Users.module.css';
import userPhoto from './../../common/userNull.jpg';

let User = ({user,folowingInProgress,unfollow,follow}) => {
    return (
            <li className={s.li} key={user.id}>
                <span>
                    <NavLink  to={'/profile/'+user.id} >
                        <img className={s.img} src={(user.photos.small != null) ? user.photos.small : userPhoto} alt="ava"/>
                    </NavLink>
                    {user.followed
                    ?
                        <button disabled={folowingInProgress.some(id => id === user.id)} 
                            onClick={() => {unfollow(user.id)}}
                        > Unfollow</button>
                    :
                        <button disabled={folowingInProgress.some(id => id === user.id)} 
                            onClick={() => {follow(user.id)}}
                        >Follow</button>
                    }     
                </span>
                <span>
                    <b>{user.name}</b>
                    <p>{user.status}</p>
                    <span>{"user.location.city"}</span>
                    <span>{"user.location.country"}</span>
                </span>
            </li>
    )
}

export default User;
