import { NavLink } from 'react-router-dom';
import s from './Header.module.css';
import React from 'react'
import { Avatar, Button, Col, Menu, Row } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import {Layout} from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { selectCurrentUserLogin, selectIsAuth } from '../../redux/auth-selectors.ts';
import {loginout} from './../../redux/auth-reducer.ts'

const {Header} = Layout;
const AppHeader:React.FC = (props) => {
    const isAuth = useSelector(selectIsAuth)
    const login = useSelector(selectCurrentUserLogin)

    const dispatch = useDispatch()
    const loginoutCallBack = () => {
        dispatch(loginout())
    }
    return ( 
        <Header className="header">
        <div className="logo" />
        <Row>
          <Col span="18">
            <Menu theme="dark" mode="horizontal">
              <Menu.Item><NavLink to='/users'>Find users</NavLink></Menu.Item>
            </Menu>
          </Col>
          {isAuth 
           ? <>
           <Col span="3">
                <div><Avatar style={{ backgroundColor: '#87d068' }} icon={<UserOutlined />} /> 
            {login} - <Button onClick={loginoutCallBack}>Log out</Button></div>
           </Col></>

           :<Col span="6"><NavLink to='/login'>Login</NavLink></Col>}
          
        </Row>
      </Header>
      /*
        <div>
            <h1 className={s.h1}>UsiChat</h1>
            <div className={s.loginBlock}>
              
            </div>
        </div>*/
    )
}
export default AppHeader;
