import React, { FormEvent, useState } from 'react'
import styles from './List.module.css'
import {v1} from 'uuid'
import { useDispatch } from 'react-redux';
import {actions} from './../../../redux/plan-reducer.ts'

const SearchInput = () => {
    const [value,setValue] = useState('')
    const dispatch = useDispatch()
    const addPurpose = (e) => {       
        if(value === "") return 
        let day = new Date().getDate().toString()
        let month = new Date().getMonth().toString()
        let year = new Date().getFullYear().toString()
        if(month.length === 1) {
            month = `0${month + 1}`
        }
        dispatch(actions.setNewPurpose({id:v1(),text:value,data:`${day}.${month}.${year}`}))
        setValue('')
    }
    return(
        <>
            <div className={styles.form}>
                <input type="text" placeholder="Ceate new part of your list" value={value} onChange={(e) => setValue(e.currentTarget.value)} className={styles.searchInput+" "+styles.inputs}/>
                <input type="submit" className={styles.submit+" "+styles.inputs} value="ADD" onSubmit={(e) => addPurpose(e)} />
            </div>
        </>
    )
}

export default SearchInput
