import React, { useState ,useEffect} from 'react'
import styles from './List.module.css'
import SearchInput from './SearchInput.tsx'
import TodosList from './TodosList.tsx'

export type Todo = {
    id:number
    title:string
    data:string
    completed:boolean
}
const List = () => {
    const options = {
        weekday: "long",
        year: "numeric",
        month: "long",
        day: "numeric",
    };
    const [todoTitle,setTodoTitle] = useState('')
    const [todos,setTodos] = useState([] as Array<Todo> )
    const addTodo = (event) => {
        if(event.key === 'Enter') {
            let newId:number;
            if(!todos[todos.length - 1]) {
                newId = 1
            } else {
                newId = (todos[todos.length - 1].id + 1)
            }
            setTodos([
                ...todos,
                {
                    id:newId,
                    title:todoTitle,
                    data:new Date().toLocaleDateString("en-KZ",options),
                    completed:false
                }
            ])
            setTodoTitle('')
        }
    }

    useEffect(() => {
        localStorage.setItem('todos',JSON.stringify(todos))
    },[todos])
    return(
        <div className={styles.list}>
            <div className={styles.form}>
                <input type="text" 
                    placeholder="Create new part of your list" 
                    value={todoTitle} 
                    onChange={(event) => setTodoTitle(event.target.value)} 
                    className={styles.searchInput+" "+styles.inputs}
                    onKeyDown={event => addTodo(event)}
                    />
                <input 
                    type="submit" 
                    className={styles.submit+" "+styles.inputs} 
                    value="ADD" 
                    onClick={event => addTodo(event)}
                    />
            </div>
            <TodosList todos={todos}/>
        </div>
    )
}

export default List
