import axios  from "axios"
import { ProfileType } from "../../types/types"

const instanse = axios.create({
    withCredentials:true,
    headers:{'API-KEY':'cafd4f76-51d0-40a7-93fa-907a2586d1e6'},
    baseURL:'https://social-network.samuraijs.com/api/1.0/'
})

export const userAPI = {
    getUsers(currentPage,pageSize) {
        return instanse.get(`users?page=${currentPage}&count=${pageSize}`).then(response => response.data)
    },
    follow(userId:number) {
        return instanse.post(`follow/${userId}`)
    },
    unfollow(userId:number) {
        return instanse.delete(`follow/${userId}`)
    },
    getProfile(profileId:number) {
        return profileAPI.getProfile(profileId);
    }

}
export const profileAPI = {
    getProfile(profileId:number) {
        return instanse.get(`profile/${profileId}`);
    },
    getStatus(profileId:number) {
        return instanse.get(`profile/status/${profileId}`);
    },
    updateStatus(status:string) {
        return instanse.put(`profile/status`,{status:status});
    },
    savePhoto(photoFile:any) {
        let formData = new FormData();
        formData.append('image',photoFile)
        return instanse.put(`profile/photo`,formData,{
            headers:{
                'Content-Type':'multipart/form-data'
            }
        });
    },
    saveProfile(profile:ProfileType) {
        return instanse.put(`profile`,profile);
    },
}
export const authAPI = {
    getData() {
        return instanse.get(`auth/me`).then(response => response.data);
    },
    login(email:string,password:string,rememberMe:boolean = false,captcha: null | string = null) {
        return instanse.post(`auth/login`,{email,password,rememberMe,captcha});
    },
    loginout() {
        return instanse.delete(`auth/login`);
    },
}
export const securityAPI = {
    getCaptchaUrl() {
        return instanse.get(`security/get-captcha-url`)
    },
}
