import './App.css';
import React from 'react'
import HeaderContainer from './components/header/HeaderContainer';
import Navbar from './components/navbar/Navbar';
import ProfileContainer from './components/profile/ProfileContainer';
import UsersContainer from './components/users/UsersContainer';
import DialogsContainer from './components/dialogs/DialogsContainer';
import Login from './components/login/Login';
import {BrowserRouter, Route, Routes} from 'react-router-dom';
import {connect} from 'react-redux'
import {initializeApp} from './redux/app-reducer'
import {compose} from 'redux'
import Preloader from './components/common/preloader/Preloader';


class App extends React.Component {

  componentDidMount() {
    this.props.initializeApp()
  }

  render() {
    if(!this.props.initialized) {
      return <Preloader />
    }
    return (
      <BrowserRouter>
      <Provider store={store}>
      <div >
        <header>
          <HeaderContainer />
        </header>
        <main>
          <Navbar /*sidebar={props.state.sidebarPage.sidebar}*//>
          <Routes>
                <Route path='/profile/:userId?' element={<ProfileContainer h='Profile'/>}/>
                <Route path='/dialogs/*' element={<DialogsContainer h='Dialogs'/>}/>
                <Route path='/users' element={<UsersContainer h='Users'/>}/>
                <Route path='/login' element={<Login h='Login'/>}/>
          </Routes>
        </main>
      </div>
      </Provider>
      </BrowserRouter>

    );
}
}

const mapStateToProps = (state) => ({
  initialized:state.app.initialized
})
export default compose (
  connect(mapStateToProps,{initializeApp}),
)(App)
