import React, { useEffect }  from 'react'
import styles from './Table.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { Navigate, useParams } from 'react-router-dom'
import { selectTables } from '../../../../redux/tables-selectors.ts'
import { tableType } from '../../../../redux/tables-reducer'

const Table = () => {
    const dispatch = useDispatch()
    const {tableId} = useParams()
    const tables = useSelector(selectTables)
    const tableData:tableType | undefined = tables.find(table => table.id === tableId)
    const arrayOfDays:Array<string | number | undefined> = Array.from({ length: parseInt(tableData?.tableData.days) }, (v, i) =>  i + 1);  
    arrayOfDays.unshift('⠀') 
    return (
        <>
        {!tableData ? <Navigate to="/tables" /> :
        <div className={styles.container}>
            <h2>{tableData.name}</h2>
            <table className={styles.table}>
                <thead>
                    <tr>
                        {arrayOfDays.map(day => <th key={day} className={styles.th}>{day}</th>)}
                    </tr>
                </thead>
                <tbody>
                    {tableData.tableData.items.map(item => <tr key={item}>
                        <td className={styles.item + ' '+ styles.td}>{item}</td>
                        {arrayOfDays?.map(cell => <td className={styles.td}>'⠀'</td>)}
                    </tr>)}
                </tbody>
            </table>
        </div>}
        </>
        
    )
}

export default Table
