import React from 'react'
import {InjectedFormProps, reduxForm} from 'redux-form'
import {GetStringCase, Input, LoginFormValuesType,createField} from '../common/formsControls/formsControsl.tsx'
import {requered,maxLengthCreator} from '../../utials/validators/validators.ts'
import {connect, useSelector} from 'react-redux'
import {login} from '../../redux/auth-reducer.ts'
import { Navigate } from 'react-router-dom'
import s from './../common/formsControls/formsControls.module.css'
import { AppStateType } from '../../redux/redux-store.ts'

const maxLength10 = maxLengthCreator(10)

type LoginFormOwnPropsType = {
    captchaUrl:string | null
}
const LoginForm:React.FC<InjectedFormProps<LoginFormValuesType,LoginFormOwnPropsType> & LoginFormOwnPropsType> = ({handleSubmit,error,captchaUrl}) => {
    return (
            <form onSubmit={handleSubmit}>
                {createField<LoginFormPropertiesType>('Login','email',Input,[requered],{},'')}
                {createField<LoginFormPropertiesType>('Password','password',Input,[requered,maxLength10],{type:'password'},'')}
                {createField<LoginFormPropertiesType>(undefined,'rememberMe',Input,[],{type:'checkbox'},'remember me')}
                {captchaUrl && <img src={captchaUrl} alt="1"/>}
                {captchaUrl && createField<LoginFormPropertiesType>('Symbols','captcha',Input,[requered],{},'')}
                {error &&
                <div className={s.formSummaryError}>
                    {error}
                </div>}
                <div>   
                    <button>Login</button>
                </div>
            </form>
    )
}

const LoginReduxForm = reduxForm<LoginFormValuesType,LoginFormOwnPropsType>({
    form:'login'
})(LoginForm)

const Login:React.FC<MapDispatchPropsType & OwnProps> = (props) => {

    const captchaUrl = useSelector( (state:AppStateType) => state.auth.captchaUrl)
    const isAuth = useSelector( (state:AppStateType) => state.auth.isAuth)
    const dispatch = useDispatch()
    const onSubmit = (formData:any) => {
        props.login(formData.email,formData.password,formData.rememberMe,formData.captcha)
    }
    if(isAuth) {
        return <Navigate to={'/profile'}/>
    }
    return ( <section>
            <h2>{props.h}</h2>
            <LoginReduxForm onSubmit={onSubmit} captchaUrl={captchaUrl}/>
        </section>
    )
}


type OwnProps = {
    h:string
}
export type LoginFormValuesType = {
    email:string
    password:string
    rememberMe:boolean
    captcha:string | any
}
type LoginFormPropertiesType = GetStringCase<LoginFormValuesType>

type MapDispatchPropsType = {
     login: (email:string,password:string,rememberMe:boolean,captcha:string | any) => void
}
export default connect(null,{login})(Login)
