import { ThunkAction } from 'redux-thunk';
import {profileAPI} from '../components/api/api.ts';
import {stopSubmit} from 'redux-form'
import {PostType,ProfileType,PhotosType} from './../types/types'
import { AppStateType } from './redux-store.js';

const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'
const SET_USER_PROFILE  = 'SET_USER_PROFILE'
const SET_STATUS  = 'SET_STATUS'
const DELETE_POST = 'DELETE_POST'
const SAVE_PHOTO_SUCCESS = 'SAVE_PHOTO_SUCCESS'
const SET_ERROR = 'SET_ERROR'

export type InitialStateType = typeof initialState

let initialState = {
  posts: [
      {'id':1, 'message':'Hi,how are you?', 'likesCount': 22},
      {'id':2, 'message':'It\'s my first post', 'likesCount': 15}
    ] as Array<PostType>,
  profile: null as ProfileType | null,
  status:"",
  error:""
}
const profileReducer = (state:InitialStateType = initialState,action:ActionsType):InitialStateType =>{
    switch(action.type){
   
    case ADD_POST: {
        let newPost = {
          'id': 5, 
          'message':action.post,
          'likesCount': 0
        };
        return {
            ...state,
            posts: [...state.posts, newPost]
        }
      }
    case SET_USER_PROFILE:{
      return {
        ...state,
        profile:action.profile
      }
    }
    case SET_STATUS:{
      return {
        ...state,
        status:action.status
      }
    }
    case DELETE_POST:{
      return {
        ...state,
        posts:state.posts.filter(p => p.id != action.postId)
      }
    }
    case SAVE_PHOTO_SUCCESS:{
      return {
        ...state,
        profile: {...state.profile , photos:action.photos} as ProfileType
      }
    }
    case SET_ERROR : {
      return {
        ...state,
        error:action.error
      }
    }
    default: {
        return state;
    }
  }
}
type addPostActionCreaterType = {
  type:typeof ADD_POST
  post:string
}

export const addPostActionCreater = (post:string):addPostActionCreaterType => ({type:ADD_POST,post});
type setUserProfileType = {
  type:typeof SET_USER_PROFILE
  profile:ProfileType
}
export const setUserProfile  = (profile:ProfileType):setUserProfileType => ({type:SET_USER_PROFILE,profile});
type setStatusType = {
  type:typeof SET_STATUS
  status:string
}
export const setStatus = (status:string):setStatusType => ({ type:SET_STATUS, status:status });
type deletePostType = {
  type:typeof DELETE_POST
  postId:number
}
export const deletePost = (postId:number):deletePostType => ({ type:DELETE_POST,postId});
type savePhotoSuccessType = {
  type:typeof SAVE_PHOTO_SUCCESS
  photos:PhotosType
}
export const savePhotoSuccess = (photos:PhotosType):savePhotoSuccessType => ({ type:SAVE_PHOTO_SUCCESS,photos});
type setErrorType = {
  type:typeof SET_ERROR
  error:string
}
//const setError = (error:string):setErrorType => ({type:SET_ERROR,error});


type ActionsType = setErrorType | savePhotoSuccessType |
deletePostType | setStatusType | setUserProfileType | addPostActionCreaterType

type ThunkType = ThunkAction<Promise<void>,AppStateType,unknown,ActionsType>
export const getProfile = (profileId:number):ThunkType => async (dispatch:any) => {
    let response = await profileAPI.getProfile(profileId)
      dispatch(setUserProfile(response.data));
    }
export const getStatus = (profileId:number) => async (dispatch:any) => {
    let response = await profileAPI.getStatus(profileId)
        dispatch(setStatus(response.data));
}
export const updateStatus = (status:string) => async (dispatch:any) => {
    try {
      let response = await profileAPI.updateStatus(status)
      if(response.data.resultCode === 0) {
        dispatch(setStatus(status));
      }
    } catch(error) {
      dispatch(setError(error.messages[0]));
    }
  }
export const savePhoto = (file:any) => async (dispatch:any) => {
  let response = await profileAPI.savePhoto(file)
      if(response.data.resultCode === 0) {
        dispatch(savePhotoSuccess(response.data.data.photos));
      }
  }
export const saveProfile = (profile:ProfileType) => async (dispatch:any,getState:any) => {
  const userId = getState().auth.userId
  let response = await profileAPI.saveProfile(profile)
      if(response.data.resultCode === 0) {
       dispatch(getProfile(userId));
      } else {
        dispatch(stopSubmit('edit-profile',{_error:response.data.messages[0]}))
        return Promise.reject(response.data.messages[0])
      }
  }
export default profileReducer;
