import { InferActionsType } from "./redux-store.ts"

const SET_NEW_TABLE = 'SET_NEW_TABLE'
const UPDATE_DATA = 'UPDATE_DATA'

type ActionsType = InferActionsType<typeof actions>
type InitialStateType = typeof initialState


type tableTypeAction = {
    items:Array<string>
    days:Array<string | number | undefined>
    data:Array<dataType>
}
export type tableType = {
    id:string
    name:string
    tableData:tableTypeAction
}
export type dataType = {
    name:string
    status: Array<string>
}

const initialState = {
    tables:[] as Array<tableType>
}

const tablesReducer = (state:InitialStateType = initialState, action:ActionsType):InitialStateType => {
    switch(action.type) {
        case SET_NEW_TABLE : {
            let newTable = {
                id:"1",
                name: `Table`,
                tableData:{
                    items:action.table.items,
                    days:action.table.days,
                    data:action.table.data
                }
            }
            let prewiousTable = state.tables[state.tables.length - 1] || null
            if(prewiousTable !== null) {
                newTable.id = String(parseInt(prewiousTable.id) + 1)
            }
            return {
                ...state,
                tables:[...state.tables,newTable]
            }
        } 
        case UPDATE_TABLE: {}
            const updatedTables = state.tables.map((table) => {
                if (table.id === action.payload.id) {
                    return action.payload;
                }
                return table;
            });
            return {
                ...state,
                tables: updatedTables,
            };
        default : {
            return state
        }
    }
}
export const actions = {
    setNewTable:(table:tableTypeAction) => ({type:SET_NEW_TABLE,table}),
    updateTable: (table:tableTypeAction) => ({type: 'UPDATE_TABLE',payload: table,})
}

export default tablesReducer
