import React from "react"
import { tableAPI } from "../api/tables/tables-api"
import { BaseThunkType, InferActionsType } from "./redux-store"

const SET_NEW_TABLE = 'SET_NEW_TABLE'
const UPDATE_TABLE = 'UPDATE_TABLE'

type ActionsType = InferActionsType<typeof actions>
type InitialStateType = typeof initialState
export type ThunkType = BaseThunkType<ActionsType>
export type tableFullType = {
    items:Array<string>
    days:Array<string | number | undefined>
    rows:Array<dataType>
    title:string
    __v:number
    _id:string
}

export type tableType = {
    items:Array<string>
    days:Array<string | number | undefined>
    rows:Array<dataType>
}
export type dataType = {
    name:string
    status: Array<string>
}

const initialState = {
    tables:[] as Array<tableType>
}

const tablesReducer = (state:InitialStateType = initialState, action:ActionsType):InitialStateType => {
    switch(action.type) {
        case SET_NEW_TABLE : {
            /*
            let newTable = {
                id:"1",
                name: `Table`,
                tableData:{
                    items:action.table.items,
                    days:action.table.days,
                    data:action.table.data
                }
            }
            let prewiousTable = state.tables[state.tables.length - 1] || null
            if(prewiousTable !== null) {
                newTable.id = String(parseInt(prewiousTable.id) + 1)
            }*/
            return {
                ...state,
                tables:[...state.tables,action.table]
            }
        } 
        case UPDATE_TABLE: {
            const updatedTables:Array<tableType> = state.tables.map((table) => {
                if (table.id === action.table._id) {
                    return action.table;
                }
                return table;
            });
            return {
                ...state,
                tables: updatedTables,
            };
        }
        default : {
            return state
        }
    }
}
export const actions = {
    setNewTable:(table:tableType) => ({type:SET_NEW_TABLE,table}),
    updateTable: (table:tableType) => ({type: UPDATE_TABLE,table})
}


export const createNewTable = ({items,days,rows}):ThunkType => async (dispatch) => {
    debugger
    let response = await tableAPI.createTable({items,days,rows});
    debugger
    if(response.status === 200) {
        dispatch(actions.setNewTable(response.data))
    }
}

export default tablesReducer
