import React from 'react'

class ProfileStatus extends React.Component {
    state = {
        editMod:false,
    }
    activateEditMode = () => {
        this.setState({
            editMod:true
        })
    }
    deactivateEditMode = () => {
        this.setState({
            editMod:false,
            status:this.props.status
        })
        this.props.updateStatus(this.statusInputRef.current.value)
    }
    render() {
        return(
            <>
                <div>
                    {!this.state.editMod?<span onDoubleClick={this.activateEditMode}>{this.props.status}</span>:
                    <input autoFocus={true} onBlur={this.deactivateEditMode} value={this.props.status}></input>}
                </div>
            </>
        )
    }
}
export default ProfileStatus;
