import so from './MyPosts.module.css';
import Post from './Post/Post';
import React ,{} from 'react';
import {reduxForm,Field, InjectedFormProps} from 'redux-form'
import {maxLengthCreator, requered} from '../../../utials/validators/validators.ts'
import {GetStringCase, Textarea, createField} from '../../common/formsControls/formsControsl.tsx'
import { LoginFormValuesType } from '../../login/Login';
import { PostType } from '../../../types/types';

const maxLength10 = maxLengthCreator(10);
type PropsType = {
}
export type AddPostValuesType = {
    newPostText:string
}
type AddPostValuesTypeKeys = GetStringCase<AddPostValuesType>
const MyPostsForm:React.FC<InjectedFormProps<AddPostValuesType,PropsType> & PropsType> = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            {createField<AddPostValuesTypeKeys>('Пост','newPostText',Textarea,[requered,maxLength10],{},'')}
            <br />
            <button>Add post</button>
        </form>
    )
}
const MyPostsReduxForm = reduxForm<AddPostValuesType,PropsType>({
    form:'ProfileAddPostForm'
})(MyPostsForm)










type MyPostPropsType = {
    posts:Array<PostType>
    addPost:(newPostText:string) => void
}

let MyPosts:React.FC<MyPostPropsType> = React.memo((props) => {
        let postsElements = props.posts.map( post => <Post message={post.message} key={post.id} likecount={post.likesCount}/>)
        
        const onAddPosts = (formData:AddPostValuesType) => {
            props.addPost(formData.newPostText);
        }   

        return (
            <div className={so.postsBlock}>
                <div>
                    My posts
                </div>
                <MyPostsReduxForm onSubmit={onAddPosts}/>
                <div className={so.posts}>
                    {postsElements}
                </div>
            </div>
        )
    })
export default MyPosts
