import {authAPI} from '../components/api/auth-api.ts';
import {FormAction, stopSubmit} from 'redux-form'
import {securityAPI} from '../components/api/security-api';
import {ResultCodeEnum, ResultCodeWithCaptchaEnum} from '../components/api/api';
import { ThunkAction } from 'redux-thunk';
import { AppStateType, BaseThunkType, InferActionsType } from './redux-store';
import { Action } from 'redux';

const SET_USER_DATA = 'SN/auth/SET_USER_DATA'
const SET_CAPTCHA_URL = 'SN/auth/SET_CAPTCHA_URL'

type ActionsType = InferActionsType<typeof actions>
type ThunkType = BaseThunkType<ActionsType | FormAction>
 
export type initialState = {
    userId:number | null
    email:string | null
    login:string | null
    isAuth:boolean
    captchaUrl:string | null
}
let initialState:initialState =  {
    userId:null,
    email:null,
    login:null,
    isAuth:false,
    captchaUrl:null,
}

export const authReducer = (state:initialState = initialState,action:ActionsType):initialState =>{
  switch(action.type) {
    case SET_USER_DATA: 
        return { 
            ...state,
            ...action.payload,
        }
    case SET_CAPTCHA_URL: 
    return { 
        ...state,
        ...action.payload
    }
    default: 
        return state;
  }
}


const actions = {
  setAuthUserData:(userId:number | null,email:string | null,login:string | null,isAuth:boolean) => ({
    type:SET_USER_DATA, payload:{userId,email,login,isAuth}
  }),
  setCaptchaUrl:(captchaUrl:string) => ({
    type:SET_CAPTCHA_URL, payload:{captchaUrl}
  })
}








export const getData = ():ThunkType => async (dispatch) => {
   let response = await authAPI.getData();
    if(response.resultCode === ResultCodeEnum.Success) {
      let {id,login,email} = response.data;
      
      dispatch(actions.setAuthUserData(id, email, login,true));
  }
}
export const login = (email:string,password:string,rememberMe:boolean,captcha:string | any):ThunkType => async (dispatch) => {
  let response = await authAPI.login(email,password,rememberMe,captcha);
  if(response.resultCode === ResultCodeEnum.Success) {
    dispatch(getData())
  } else {
    if (response.resultCode === ResultCodeWithCaptchaEnum.CaptchaIsRequired) {
      dispatch(getCaptchaUrl())
    }
    let message = response.messages.length > 0 ?response.messages[0]:'Some error'
    dispatch(stopSubmit('login',{_error:message})) 
  }
}
export const getCaptchaUrl = ():ThunkType => async (dispatch) => {
  let response = await securityAPI.getCaptchaUrl();
  const captchaUrl = response.url
  dispatch(actions.setCaptchaUrl(captchaUrl))
}
export const loginout = ():ThunkType => async (dispatch) => {
    let response = await authAPI.loginout()
    if(response.data.resultCode === 0) {
      dispatch(actions.setAuthUserData(null, null, null,false));
  }
}
export default authReducer;
