import React from 'react';
import { NavLink } from 'react-router-dom';
import s from './Users.module.css';
import Paginator from './../common/paginator/Paginator'

let Users = (onPageChanged,currentPage,totalUsersCount,pageSize,selectedPage,...props) => {
        return (
            <section>
                <h2>{props.h}</h2>
                <Paginator currentPage={currentPage} selectedPage={selectedPage} onPageChanged={onPageChanged} totalUsersCount={totalUsersCount} pageSize={pageSize}/>
                <ul>
                    {
                    props.users.map( u => <li className={s.li} key={u.id}>
                        <span>
                            <NavLink  to={'/profile/'+u.id} >
                                <img className={s.img} src={u.photos.small != null?u.photos.small:'/userNull.jpg'} alt="ava"/>
                            </NavLink>



                            {u.followed
                            ?
                                <button disabled={props.folowingInProgress.some(id => id === u.id)} 
                                    onClick={() => { props.unfollow(u.id)}}
                                > Unfollow</button>
                            :
                                <button disabled={props.folowingInProgress.some(id => id === u.id)} 
                                    onClick={() => {props.follow(u.id)}}
                                >Follow</button>
                            }     



                        </span>
                        <span>
                            <b>{u.name}</b>
                            <p>{u.status}</p>
                            <span>{"u.location.city"}</span>
                            <span>{"u.location.country"}</span>
                        </span>
                    </li>)
                    }
                </ul>
            </section>
        )
    }

export default Users;
