import React from 'react'
import {createField,Input} from './../../common/formsControls/formsControsl'
import {Contact} from './Profileinfo'
const ProfileDataForm = ({profile,isOwner,goToEditMode}) => {
    return <form>
        {isOwner && <button onClick={goToEditMode}>Save</button>}
            <div>
                <b>Full name: </b>{createField("Full name","fullname",[], Input)}
            </div>
            <div>
                <b>Looking for a job: </b>{profile.lookingForAJob ? 'yes' : 'no'}
            </div>
            {profile.lookingForAJob &&
                <div>
                    <b>Skills: </b> {profile.lookingForAJobDescription}
                </div>
            }
            <div>
                <b>About me: </b> {profile.aboutMe || '---'}
            </div>
            <div>
                <b>Contacts: </b> {Object.keys(profile.contacts).map(key => {
                    return  <Contact contactTitle={key} contactValue={profile.contacts[key]}/>
                })}
            </div>
        </form>
}
constProfileDataForm
export default ProfileDataForm
