import React, { useState } from 'react'
import styles from './List.module.css'
import SearchInput from './SearchInput.tsx'
import ListOfTodos from './ListOfTodos.tsx'

const List = () => {
    const [todoTitle,setTodoTitle] = useState('')
    const [todos,setTodos] = useState([
        {id:1,title:'First todo',data:'01.07.2023',completed:false},
        {id:2,title:'Second todo',data:'06.07.2023',completed:false}
    ])
    const addTodo = (event) => {
        if(event.key === 'Enter') {
            setTodos([
                ...todos,
                {
                    id:todos[-1].id + 1,
                    title:todoTitle,
                    data:'08.07.2023'
                }

            ])
        }
    }
    return(
        <div className={styles.list}>
            <div className={styles.form}>
                <input type="text" 
                    placeholder="Create new part of your list" 
                    value={todoTitle} 
                    onChange={(event) => setTodoTitle(event.target.value)} 
                    className={styles.searchInput+" "+styles.inputs}
                    onKeyDown={event => addTodo(event)}
                    />
                <input 
                    type="submit" 
                    className={styles.submit+" "+styles.inputs} 
                    value="ADD" 
                    onClick={event => addTodo(event)}
                    />
            </div>
            <ListOfTodos todos={todos}/>
        </div>
    )
}

export default List
