import React ,{useEffect,useState}from 'react'
import { Avatar, Button } from 'antd';
import { useDispatch ,useSelector} from 'react-redux';
import { startMessagesListening ,sendMessage,stopMessagesListening} from '../../redux/chat-reducer.ts';
import { AppStateType } from '../../redux/redux-store';

export type ChatMessageType =   {
    message:string
    photo:string
    userId:number
    userName:string
  }
const ChatPage:React.FC = () => {
    return (
        <Chat />
    )
}
const Chat:React.FC = () => {
    let dispatch = useDispatch()
    useEffect(() => {
    dispatch(startMessagesListening())
    return () => {
        dispatch(stopMessagesListening())
    }
    },[])
    return (    
    <div> 
        <Messages/>
        <AddMessageForm />
     </div>
    )
}
const Messages:React.FC = () => {
    const messages = useSelector((state:AppStateType) => state.chat.messages)
    return ( 
    <div style={{height:'400px',overflowY:'auto'}}>
        {messages.map((m,index) => <Message key={index} message={m}/>)}
     </div>
    )
}
const Message:React.FC<{message:ChatMessageType}> = ({message}) => {
    return (<div>
        <img src={message.photo} alt="ava" style={{width:'45px',marginRight:'10px'}}/><b>{message.userName}</b>
        <br />
        {message.message}
        <hr />
    </div>)
}
const AddMessageForm:React.FC = () => {
    const [message,setMessage] = useState('')
    const [readyStatus,setReadyStatus] = useState<'pending' | 'ready'>('pending')
    c
    
    const dispatch = useDispatch()
    
    const sendMessage = () => {
        if(!message) return
        dispatch(sendMessage(message))
        setMessage('')
    }
    return ( 
    <div>
        <div><textarea onChange={(e) => setMessage(e.currentTarget.value)} value={message}></textarea></div>
        <Button onClick={sendMessage} disabled={false}>Send</Button>
     </div>
    )
}

export default ChatPage
