import so from './MyPosts.module.css';
import Post from './Post/Post';
import React from 'react';
import {addPostActionCreater,updateNewPostTextActionCreater} from '../../../redux/profile-reducer'



const MyPosts = (props) => {

    let postsElements = props.posts.map( post => <Post message={post.message} likecount={post.likesCount}/>)
    let newPostElement = React.createRef();
    let onAddPosts = () => { 
        props.addPost()
    }
    let onPostChange = () => {
        let text = newPostElement.current.value;
        props.updateNewPostText(text);
    }
    return (
        <div className={so.postsBlock}>
            <div>
                My posts
            </div>
            <div>
                <textarea onChange={onPostChange} ref={newPostElement} value={props.newPostText} />
                <br />
                <button onClick={onAddPosts}>Add post</button>
            </div>
            <div className={so.posts}>
                {postsElements}
            </div>
        </div>
    )
}

export default MyPosts
