import React from 'react';
import Paginator from '../common/paginator/Paginator.tsx'
import User from './User.tsx'
import {UserType} from './../../types/types.ts'
import { Formik ,Form , Field} from 'formik';
import { isSubmitting } from 'redux-form';

type Props = {
    onPageChanged:(pageNubmber:number) => void
    currentPage:number
    totalUsersCount:number
    h:string
    pageSize:number
    users:Array<UserType>
    folowingInProgress:Array<number>
    unfollow:(userId:number) => void
    follow:(userId:number) => void
}
let Users:React.FC<Props> = ({onPageChanged,currentPage,totalUsersCount,pageSize,users,...props}) => {
    return (
        <section>
            <h2>{props.h}</h2>
            <UsersSearchForm />
            <Paginator currentPage={currentPage} onPageChanged={onPageChanged} totalItemsCount={totalUsersCount} pageSize={pageSize} portionSize={3}/>
            <ul>
                {
                users.map( u => <User user={u} 
                        key={u.id} 
                        folowingInProgress={props.folowingInProgress} 
                        unfollow={props.unfollow} 
                        follow={props.follow}
                    />)
                }
            </ul>
        </section>
    )
}

type UsersSearchFormObjectType = {
    term:string
}
const UsersSearchFormValidate = (values:any) => {
    const errors = {}
    return errors
}
const UsersSearchForm = () => {
    const submit = (values:UsersSearchFormObjectType,{setSubmitting}:{setSubmitting :(isSubmitting:boolean) => void}) => {
        alert(JSON.stringify(values,null,2))
        setSubmitting(false)
    }
    return <div>
            <Formik 
                onSubmit={submit} 
                validate={UsersSearchFormValidate}
                initialValues={{term:""}}
            >
                {((isSubmitting)) }
                <Form>
                    <Field type="text" name="term" />
                    <button type="submit">Find</button>
                </Form>
            </Formik>
        </div>
}

export default Users;
