import { Todo } from "../components/planPage/types/types"
import { InferActionsType } from "./redux-store"

type ActionsType = InferActionsType<typeof actions>
type InitialStateType = typeof initialState

const SET_NEW_TODO = 'SET_NEW_PURPOSE'
const DELETE_TODO = 'DELETE_PURPOSE'

const initialState = {
    todos:[] as Array<Todo>
}

const planReducer = (state:InitialStateType = initialState,action:ActionsType):InitialStateType => {
    switch(action.type) {
        case SET_NEW_TODO : {
            return { ...state,
                todos:[action.purpose,...state.todos]
            }
        }
        case DELETE_TODO : {
            return {
                ...state,
                todos:state.todos.filter((item) => item != action.purpose)
            }
        }
        default: {
            return state;
        }
    }
}

export const actions = {
    setNewTodo:(todo:Todo) => ({type:SET_NEW_TODO,todo}),
    deletePurpose:(purpose:Todo) => ({type:DELETE_TODO,purpose}),
}

export default planReducer
