export type FieldlValidType = (value:string) => string | undefined

export const requered:FieldlValidType = (value) => {
    if(value) return undefined
    return "Обязательно к заполнению"
}
export const maxLengthCreator = (maxLength:number):FieldlValidType => (value) => {
    if(value && value.length > maxLength) return `Максимальное число символов ${maxLength}`
    return undefined
}
