import {getData} from './auth-reducer'

const SET_INITIALIZED = 'SET_INITIALIZED'


let initialState =  {
    initialized:false,
}

export const appReducer = (state = initialState,action) =>{
  switch(action.type) {
    case SET_INITIALIZED: 
        return { 
            ...state,
            initialized:true
        }
    default: 
        return state;
  }
}
export const initializedSucces = () => ({type:SET_INITIALIZED});

export const initializeApp = () => {
  return (dispatch) =>  {
    let promise = dispatch(getData());
    promise.then(() => dispatch(initializedSucces()))
   
  }
}


export default appReducer;
