import React,{useEffect} from 'react'
import {useDispatch,useSelector} from 'react-redux'
import styles from '../FormCreateTable.module.css'
import { selectDays, selectItems } from '../../../../redux/createTable-selectors.ts'
import {useNavigate} from 'react-router-dom'
import { actions } from '../../../../redux/tables-reducer.ts'
import { tableCreateActions } from '../../../../redux/createTable-reducer.ts'

const Data = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const items = useSelector(selectItems)
    const days = useSelector(selectDays)

    const removeItem = (value:string) => {
        dispatch(tableCreateActions.deleteItem(value))
    }
    const arrayOfDays:Array<string | number | undefined> = Array.from({ length: parseInt(days) }, (v, i) =>  i + 1);  
    const arrayOfItemsStatus = [] as Array<Array<string>>
    if(items.length > 0) {
        for(let cnt = 1; cnt <= items.length ;cnt++) {
                arrayOfItemsStatus.push([])
            for(let cnt1 = 1; cnt1 <= arrayOfDays.length ;cnt1++){
                arrayOfItemsStatus[cnt - 1].push('ㅤ')
            }
        } 
    }
   /*
   if(days || items.length !== 0) {
        const arrayOfData = [] as Array<Array<string>>

        for(let cnt = 0; cnt <= items.length ;cnt++) {
            arrayOfData.push([items[cnt]])
        }    
        console.log(arrayOfData)
        for(let cnt = 0;cnt <= arrayOfData.length ;cnt++) {
            arrayOfData[cnt].push('0')
        } 
        console.log(arrayOfData)
    }
    */
    
    
    const createTable = () => {
        if(parseInt(days) < 0 || parseInt(days) > 7 )  return
        if(items.length < 1)  return
        dispatch(actions.setNewTable({items:items, days:arrayOfDays,status:arrayOfItemsStatus}))
        dispatch(tableCreateActions.deleteData())
        navigate('/tables')
    }
    return (
        <>
            <section className={styles.section}>
                <div className={styles.items}>
                    <b className={styles.b}>Your`s types:</b>
                    {items && <ol>
                        {items.map(item => <li className={styles.li} key={item} onClick={(e) => removeItem(e.currentTarget.innerHTML)}>{item}</li>)}
                    </ol>}
                </div>
                <div className={styles.days}>
                    <b className={styles.b}>Days: {days}</b>
                </div>
            </section>
            <input type="submit" value="Create" className={styles.inputs + ' ' + styles.submit} onClick={ () => createTable()}/>
        </>
    )
}

export default Data
