import { ThunkAction } from 'redux-thunk';
import {profileAPI} from '../components/api/profile-api.ts';
import {stopSubmit} from 'redux-form'
import {PostType,ProfileType,PhotosType} from './../types/types'
import { AppStateType, BaseThunkType, InferActionsType } from './redux-store.ts';

const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'
const SET_USER_PROFILE  = 'SET_USER_PROFILE'
const SET_STATUS  = 'SET_STATUS'
const DELETE_POST = 'DELETE_POST'
const SAVE_PHOTO_SUCCESS = 'SAVE_PHOTO_SUCCESS'
const SET_ERROR = 'SET_ERROR'

export type InitialStateType = typeof initialState
type ActionsType = InferActionsType<typeof actions>
type ThunkType = BaseThunkType<ActionsType>




let initialState = {
  posts: [
      {'id':1, 'message':'Hi,how are you?', 'likesCount': 22},
      {'id':2, 'message':'It\'s my first post', 'likesCount': 15}
    ] as Array<PostType>,
  profile: null as ProfileType | null,
  status:"",
  error:""
}
const profileReducer = (state:InitialStateType = initialState,action:ActionsType):InitialStateType =>{
    switch(action.type){
   
    case ADD_POST: {
        let newPost = {
          'id': 5, 
          'message':action.post,
          'likesCount': 0
        };
        return {
            ...state,
            posts: [...state.posts, newPost]
        }
      }
    case SET_USER_PROFILE:{
      return {
        ...state,
        profile:action.profile
      }
    }
    case SET_STATUS:{
      return {
        ...state,
        status:action.status
      }
    }
    case DELETE_POST:{
      return {
        ...state,
        posts:state.posts.filter(p => p.id != action.postId)
      }
    }
    case SAVE_PHOTO_SUCCESS:{
      return {
        ...state,
        profile: {...state.profile , photos:action.photos} as ProfileType
      }
    }
    case SET_ERROR : {
      return {
        ...state,
        error:action.error
      }
    }
    default: {
        return state;
    }
  }
}
type addPostActionCreaterType = {
  type:typeof ADD_POST
  post:string
}
const actions = {
  addPostActionCreater:(post:string) => ({type:ADD_POST,post} as const),
  setUserProfile :(profile:ProfileType) => ({type:SET_USER_PROFILE,profile} as const),
  setStatus:(status:string) => ({ type:SET_STATUS, status:status } as const),
  deletePost:(postId:number) => ({ type:DELETE_POST,postId} as const),
  savePhotoSuccess:(photos:PhotosType) => ({ type:SAVE_PHOTO_SUCCESS,photos}),
  setError:(error:string) => ({type:SET_ERROR,error})
}


export const getProfile = (profileId:number):ThunkType => async (dispatch) => {
    let response = await profileAPI.getProfile(profileId)
      dispatch(actions.setUserProfile(response));
    }
export const getStatus = (profileId:number):ThunkType => async (dispatch) => {
    let response = await profileAPI.getStatus(profileId)
        dispatch(actions.setStatus(response));
}
export const updateStatus = (status:string):ThunkType => async (dispatch) => {
    try {
      let response = await profileAPI.updateStatus(status)
      if(response.resultCode === 0) {
        dispatch(actions.setStatus(status));
      }
    } catch(error) {
      dispatch(actions.setError(error.messages[0]));
    }
  }
export const savePhoto = (file:any):ThunkType => async (dispatch) => {
  let response = await profileAPI.savePhoto(file)
      if(response.resultCode === 0) {
        dispatch(actions.savePhotoSuccess(response.data.photos));
      }
  }
export const saveProfile = (profile:ProfileType):ThunkType => async (dispatch,getState) => {
  const userId = getState().auth.userId
  let response = await profileAPI.saveProfile(profile)
      if(response.resultCode === 0) {
       dispatch(getProfile(userId));
      } else {
        dispatch(stopSubmit('edit-profile',{_error:response.messages[0]}))
        return Promise.reject(response.messages[0])
      }
  }
export default profileReducer;
