import {rerenderEntireTree} from '../render';

let state = {
  profilePage: {
    posts: [
      {'id':'1', 'message':'Hi,how are you?', 'likesCount': 22},
      {'id':'2', 'message':'It\'s my first post', 'likesCount': 15}
    ],
    newPostText:'hhh'
  },
  dialogsPage: {
    messages: [
      {'id':1, 'message': 'Hi'},
      {'id':2, 'message': 'How are you?'},
      {'id':3, 'message': 'Yo'},
      {'id':4, 'message': 'Yo'}
    ],
    dialogs: [
      {'id':1, 'name': 'Dima'},
      {'id':2, 'name': 'Roof'},
      {'id':3, 'name': 'Andry'},
      {'id':4, 'name': 'Roma'}
    ]
  },
  sidebarPage:{
    sidebar: [
    {'id':1, 'name': 'Sveta', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'},
    {'id':2, 'name': 'Roma', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'},
    {'id':3, 'name': 'Dima', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'}
  ],
  }
}

export let addPost = (postMessage) => {
  let newPost = {
    'id': 5, 
    'message':postMessage,
    'likesCount': 0
  };
  state.profilePage.posts.push(newPost)

 rerenderEntireTree(state)
}
export let changeNPost = (postMessage) => {
  let newPost = {
    'id': 5, 
    'message':postMessage,
    'likesCount': 0
  };
  state.profilePage.posts.push(newPost)

 rerenderEntireTree(state)
}
export default state;
