import { ResponseType } from './../components/api/api';
import { Dispatch } from 'redux';
import {userAPI} from '../components/api/users-api.ts';
import {updateObjectInArray} from '../utials/object-helpers'
import {UserType} from './../types/types.ts'
import { AppStateType, BaseThunkType, InferActionsType } from './redux-store.ts';
import { ThunkAction } from 'redux-thunk';

const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET_USERS'
const SET_CURRENT_PAGE = ' SET_CURRENT_PAGE'
const SET_TOTAL_USERS_COUNT = 'SET_TOTAL_USERS_COUNT'
const TOGGLE_IS_FETCHING = 'TOGGLE_IS_FETCHING'
const TOGGLE_IS_FOLLOWING_PROGRESS = 'TOGGLE_IS_FOLLOWING_PROGRESS'

let initialState = {
  users:[] as Array<UserType>,
  pageSize: 5,
  totalUsersCount: 0,
  currentPage:1,
  isFetching:true,
  folowingInProgress:[] as Array<number>//array of users id
}
export type initialStateType = typeof initialState
type ActionsType = InferActionsType<typeof actions>
type ThunkType = BaseThunkType<ActionsType>

const usersReducer = (state:initialStateType = initialState,action:ActionsType):initialStateType =>{
    switch(action.type){
      
        case FOLLOW: {
          return { 
            ...state, 
            users: updateObjectInArray(state.users,action.userId,'id',{followed:true})
          }
        }
        case UNFOLLOW: {
          return { 
            ...state, 
            users: updateObjectInArray(state.users,action.userId,'id',{followed:false})
          }
        }
        case SET_USERS : {
          return {
            ...state, users:action.users 
          }
        }
        case SET_CURRENT_PAGE : {
          return {
            ...state, currentPage:action.currentPage 
          }
        }
        case SET_TOTAL_USERS_COUNT : {
          return {
            ...state, totalUsersCount:action.totalUsersCount
          }
        }
        case TOGGLE_IS_FETCHING : {
          return {
            ...state, isFetching:action.isFetching
          }
        }
        case TOGGLE_IS_FOLLOWING_PROGRESS : {
          return {
            ...state, 
            folowingInProgress:action.isFetching
            ?[...state.folowingInProgress ,action.userId]
            :[state.folowingInProgress.filter(id => id !== action.userId)]
          }
        }
        default: {
            return state;
        }
  }
}

export const actions = {
  acceptFollow:(userId:number) => ({type:FOLLOW , userId} as const),
  acceptUnfollow: (userId:number) => ({type:UNFOLLOW,userId} as const),
  setUsersAC: (users:Array<UserType>) => ({type:SET_USERS,users} as const),
  setCurrentPage: (currentPage:number) => ({type:SET_CURRENT_PAGE, currentPage} as const),
  setFilter: (term:string) => ({type:SET_FILTER, term} as const),
  setTotalUsersCount: (totalUsersCount:number) => ({type:SET_TOTAL_USERS_COUNT, totalUsersCount} as const),
  toggleIsFetching: (isFetching:boolean) => ({type:TOGGLE_IS_FETCHING, isFetching} as const),
  toggleFollowingProgress:(isFetching:boolean,userId:number) => ({type:TOGGLE_IS_FOLLOWING_PROGRESS, isFetching,userId} as const)
}

export const getUsers = (currentPage:number,pageSize:number,term:string):ThunkType => { 
  return async (dispatch,getState) => {
    dispatch(actions.toggleIsFetching(true))
    dispatch(actions.setCurrentPage(currentPage))
    let data = await userAPI.getUsers(currentPage,pageSize)
        dispatch(actions.toggleIsFetching(false))
        dispatch(actions.setUsersAC(data.items));
        dispatch(actions.setTotalUsersCount(data.totalCount));
  }
}

const _followUnfollowFlow = async (dispatch:Dispatch<ActionsType>,userId:number,apiMethod:(userId:number) => Promise<ResponseType>,AC:(userId:number) => ActionsType ) => {
  dispatch(actions.toggleFollowingProgress(true,userId));
  let response = await apiMethod(userId)
    if(response.resultCode === 0) {
      dispatch(AC(userId));
    }
  dispatch(actions.toggleFollowingProgress(false,userId));
}

export const follow = (userId:number):ThunkType => { 
  return async (dispatch) =>  {
    await _followUnfollowFlow(dispatch,userId,userAPI.follow.bind(userAPI),actions.acceptFollow);
  }
}
export const unfollow = (userId:number):ThunkType => { 
  return async (dispatch) =>  {
    await _followUnfollowFlow(dispatch,userId,userAPI.unfollow.bind(userAPI),actions.acceptUnfollow);
  }
}
export default usersReducer;
