import { purposeType } from "../components/planPage/types/types"
import { InferActionsType } from "./redux-store"

type ActionsType = InferActionsType<typeof actions>
type InitialStateType = typeof initialState

const SET_TYPE = 'SET_TYPE'
const DELETE_TYPE = 'DELETE_TYPE'

const initialState = {
    items:[] as Array<string>
}

const createTableReducer = (state:InitialStateType = initialState,action:ActionsType):InitialStateType => {
    switch(action.type) {
        case SET_TYPE : {
            return { ...state,
                items:[action.item,...state.items]
            }
        }
        case DELETE_TYPE : {
            return {
                ...state,
                items:state.items.filter((i) => i != action.item)
            }
        }
        default: {
            return state;
        }
    }
}

export const actions = {
    setNewItem:(item:string) => ({type:SET_TYPE,item}),
    deleteItem:(item:string) => ({type:DELETE_TYPE,item})
}

export default createTableReducer
