import React from 'react'
import s from './formsControls.module.css'

export const Textaria = (props) => {
    const {input,meta,...restProps} = props;
    return <FormControl {...props}><textarea {...input} {...restProps}/></FormControl>
}
export const Input = (props) => {
    const {input,meta,...restProps} = props;
    return <FormControl {...props}><input {...input} {...restProps} /></FormControl>
}
const FormControl = ({input,meta,child,...props}) => {
    const hasError = meta.touched && meta.error
    return (
        <div>
            <div className={(hasError?s.error:'')} >{props.children}</div>
            { hasError && <span className={s.span}>{meta.error}</span>}
        </div>
        
    )
}

export const createField = (placeholder,name) => {
    <Field placeholder='Login' name='email' component={Input} validate={[requered]}/>
}
